#设置客户端连接的编码
SET NAMES UTF8;
#丢弃数据库
DROP DATABASE IF EXISTS qqmusic;
#创建数据库
CREATE DATABASE qqmusic CHARSET UTF8;
#进入该数据库
USE qqmusic;

#创建保存用户数据的表
CREATE TABLE user(
uid INT PRIMARY KEY AUTO_INCREMENT,
uname CHAR(20),
uqq CHAR(20),
upwd CHAR(50),
email CHAR(20),
phone CHAR(11)
);

