#创建保存歌曲数据的表
CREATE TABLE song(
id INT PRIMARY KEY AUTO_INCREMENT,
songurl CHAR(200),
songname CHAR(20),
singer CHAR(20),
album CHAR(50),
lyic VARCHAR(99999)
);
